import React, { useState, useEffect } from "react";

// Base styles for the component
const alertMessage = {
  marginTop: "5px",
};

const highlight = {
  border: "2px solid red",
  backgroundColor: "red",
};

const centerContainerStyle = {
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  justifyContent: "center",
  height: "50vh",
  textAlign: "center",
};

const addSkillButtonStyle = {
  backgroundColor: "#525252",
  border: "1px solid #333",
  color: "white",
  borderRadius: "5px",
  marginLeft: "10px",
  cursor: "pointer",
};

const formBoxStyle = {
  border: "1px solid #ccc",
  padding: "20px",
  backgroundColor: "#f5f5f5",
};

const formGroupStyle = {
  marginBottom: "10px",
  display: "flex",
  alignItems: "center",
};

const sharpEdgeButtonStyle = {
  backgroundColor: "#525252",
  border: "1px solid #333",
  padding: "10px 20px",
  color: "white",
  borderRadius: "5px",
  cursor: "pointer",
  marginTop: "10px",
};

const inputStyle = {
  width: "100%",
  padding: "10px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  boxSizing: "border-box",
};

const skillTagStyle = {
  backgroundColor: "#333",
  color: "white",
  borderRadius: "0",
  padding: "5px 10px",
  margin: "0 5px",
};

const buttonGroupStyle = {
  display: "flex",
  justifyContent: "space-between",
  marginTop: "10px",
};

function CandidateRegistration() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    role: "",
    skill: "",
    skills: [],
  });

  const [registrationStatus, setRegistrationStatus] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [isFormValid, setIsFormValid] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const highlightInput = true;

  const handleSkillChange = (e) => {
    setFormData({ ...formData, skill: e.target.value });
  };
  const handleAddSkill = () => {
    if (formData.skill && formData.skills.length < 5) {
      setFormData({
        ...formData,
        skills: [...formData.skills, formData.skill],
        skill: "",
      });
    }
  };

  const handleReset = () => {
    setFormData({
      name: "",
      email: "",
      role: "",
      skill: "",
      skills: [],
    });
    setErrorMessage("");
  };
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      const newCandidate = {
        id: Date.now(), // or another unique identifier
        name: formData.name,
        email: formData.email,
        role: formData.role,
        skills: formData.skills,
      };
      const updatedCandidates = [...candidates, newCandidate];
      setCandidates(updatedCandidates);
      sessionStorage.setItem("candidates", JSON.stringify(updatedCandidates));
      setRegistrationStatus("success");
      setFormData({
        name: "",
        email: "",
        role: "",
        skill: "",
        skills: [],
      });
      setErrorMessage("");
    } else {
      setRegistrationStatus("failed");
      setErrorMessage("Form is not valid. Please check your input.");
    }
  };
  useEffect(() => {
    localStorage.setItem("candidates", JSON.stringify(candidates));
  }, [candidates]);
  useEffect(() => {
    const storedCandidates = localStorage.getItem("candidates");
    if (storedCandidates) {
      setCandidates(JSON.parse(storedCandidates));
    }
  }, []);

  useEffect(() => {
    // Save candidates to localStorage whenever candidates state changes
  }, [candidates]);

  useEffect(() => {
    // Validation logic here
    const isValidName = /^[a-zA-Z0-9 ]+$/.test(formData.name);
    const isValidRole = /^[a-zA-Z0-9 ]+$/.test(formData.role);
    const isValidEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email);
    const hasSkills = formData.skills.length > 0;
    const isEmailUnique = !candidates.some(
      (candidate) => candidate.email === formData.email
    );

    const formIsValid =
      isValidName && isValidRole && isValidEmail && hasSkills && isEmailUnique;
    setIsFormValid(formIsValid);

    if (!isEmailUnique) {
      setErrorMessage("Email already exists");
    } else {
      setErrorMessage("");
    }
  }, [formData, candidates]);
  return (
    <div style={centerContainerStyle}>
      <div style={formBoxStyle}>
        <div data-testid="registration-component" style={formBoxStyle}>
          <form onSubmit={handleFormSubmit}>
            <div className="form-group" style={formGroupStyle}>
              <input
                type="text"
                name="name"
                placeholder="Name"
                required
                style={inputStyle}
                data-testid="form-input-name"
              />
            </div>
            <div className="form-group" style={formGroupStyle}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                data-testid="form-input-email"
                required
                style={{ ...inputStyle, ...(highlightInput ? highlight : {}) }}
              />
            </div>
            <div className="form-group" style={formGroupStyle}>
              <input
                type="text"
                name="role"
                placeholder="Role"
                required
                style={inputStyle}
              />
            </div>
            <div className="form-group" style={formGroupStyle}>
              <input
                data-testid="form-input-skill"
                type="text"
                name="skill"
                placeholder="Skill"
                value={formData.skill}
                onChange={handleSkillChange}
                style={inputStyle}
              />
              <button
                type="button"
                data-testid="add-btn"
                style={addSkillButtonStyle}
                onClick={handleAddSkill}
                disabled={!formData.skill || formData.skills.length >= 5}
              >
                Add Skill
              </button>
            </div>
            <div>
              {formData.skills.map((skill, index) => (
                <span data-testid="skill-tag" style={skillTagStyle}>
                  {skill}
                </span>
              ))}
            </div>
            <div className="form-group" style={alertMessage}>
              {errorMessage}
            </div>
            <button
              data-testid="reset-btn"
              type="button"
              style={sharpEdgeButtonStyle}
              onClick={handleReset}
            >
              Reset
            </button>
            <button
              data-testid="submit-btn"
              type="submit"
              style={sharpEdgeButtonStyle}
              disabled={!isFormValid}
            >
              Register
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default CandidateRegistration;
